mapper.select:
select balance, holder
	from account
		where account_no = #{accountNo}

select account_no
	from account
		where account_no = #{accountNo} and password = #{password}

select account_no, holder, balance
	from account
		where id = #{id}

mapper.update:
update account
	set balance = balance - #{ amount }
		where account_no = #{ accountNo }

update account
	set balance = balance + #{ amount }
		where account_no = #{ accountNo }
